        //3.1Folder de archivos estaticos
        static:path.join(__dirname,"public"),
        //puerto del servidor de desarrollo
        port:3000,
        //3.3 definiendo el host
        host:"localhost"
    },
    //Agtrdando un modulo webpack
    module:{
        rules:[
            {
                test:/\.js$/,
                exclude: /(node_modules|bower_components)/,
